  }
  loadLivros() {
    this.apiService.get('/livros').subscribe({
      next: data => {
        this.livros = data;
      },
      error: error => {
        this.errorMessage = 'Erro ao carregar livros: ' + error.message;
      }
    });
  }
  searchGoogleBooks() {
    if (!this.googleSearchQuery.trim()) return;
    this.apiService.get(`/livros/google-books?query=${encodeURIComponent(this.googleSearchQuery)}`).subscribe({
      next: data => {
        this.googleBooks = data;
      },
      error: error => {
        this.errorMessage = 'Erro ao buscar no Google Books: ' + error.message;
      }
    });
  }
  addFromGoogleBooks(book) {
    this.apiService.post('/livros/google-books/add', book).subscribe({
      next: data => {
        this.livros.push(data);
        this.successMessage = 'Livro adicionado com sucesso!';
        this.googleBooks = this.googleBooks.filter(b => b.isbn !== book.isbn);
      },
      error: error => {
        this.errorMessage = 'Erro ao adicionar livro: ' + error.message;
      }
    });
  }
