import { Component } from '@angular/core';
import { RouterOutlet, RouterModule } from '@angular/router';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [CommonModule, RouterOutlet, RouterModule],
  template: `
    <nav class="navbar navbar-expand-lg navbar-dark bg-primary">
      <div class="container">
        <a class="navbar-brand" routerLink="/">
          <i class="fas fa-book me-2"></i>
          Sistema de Biblioteca
        </a>
        <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav">
          <span class="navbar-toggler-icon"></span>
        </button>
        <div class="collapse navbar-collapse" id="navbarNav">
          <ul class="navbar-nav ms-auto">
            <li class="nav-item">
              <a class="nav-link" routerLink="/" routerLinkActive="active" [routerLinkActiveOptions]="{exact: true}">
                <i class="fas fa-home me-1"></i>Início
              </a>
            </li>
            <li class="nav-item">
              <a class="nav-link" routerLink="/usuarios" routerLinkActive="active">
                <i class="fas fa-users me-1"></i>Usuários
              </a>
            </li>
            <li class="nav-item">
              <a class="nav-link" routerLink="/livros" routerLinkActive="active">
                <i class="fas fa-book me-1"></i>Livros
              </a>
            </li>
            <li class="nav-item">
              <a class="nav-link" routerLink="/emprestimos" routerLinkActive="active">
                <i class="fas fa-exchange-alt me-1"></i>Empréstimos
              </a>
            </li>
          </ul>
        </div>
      </div>
    </nav>

    <main class="container mt-4">
      <router-outlet></router-outlet>
    </main>

    <footer class="bg-light text-center py-3 mt-5">
      <div class="container">
        <p class="mb-0">&copy; 2024 Sistema de Gestão de Biblioteca. Todos os direitos reservados.</p>
      </div>
    </footer>
  `,
  styles: [`
    .navbar-brand {
      font-weight: bold;
    }

    .nav-link {
      font-weight: 500;
    }

    .nav-link.active {
      font-weight: bold;
    }

    main {
      min-height: calc(100vh - 200px);
    }
  `]
})
export class AppComponent {
  title = 'Sistema de Gestão de Biblioteca';
}
